mapper.select:
		select `ft_client_id`,`client_user_id`,`marketer_id`,`audit_status`,`audit_remark`,`client_no`,`service_contract_no`,`servicer_id`,`operator_id`,`client_type`,`gmt_create`,`gmt_modified`,`is_delete` from ft_client

		  		   and ft_client_id = #{ftClientId} 
		  		   and client_user_id = #{clientUserId} 
		  		   and marketer_id = #{marketerId} 
		  		   and audit_status = #{auditStatus} 
		  		   and audit_remark = #{auditRemark} 
		  		   and client_no = #{clientNo} 
		  		   and service_contract_no = #{serviceContractNo} 
		  		   and servicer_id = #{servicerId} 
		  		   and operator_id = #{operatorId} 
		  		   and client_type = #{clientType} 
		  		   and gmt_create = #{gmtCreate} 
		  		   and gmt_modified = #{gmtModified} 
		  		   and is_delete = #{isDelete} 



                order by ${sort} ${order}


                order by ft_client_id desc



			limit #{offset}, #{limit}

select count(*) from ft_client

  		   and ft_client_id = #{ftClientId} 
